// =====================================
// Language Management Functions
// ===================================== 

// Get saved language from localStorage or default to 'en'
function getSavedLanguage() {
    return localStorage.getItem('preferredLanguage') || 'en';
}

// Save language preference to localStorage
function saveLanguage(lang) {
    localStorage.setItem('preferredLanguage', lang);
}

// Update all text elements based on selected language
function updatePageLanguage(lang) {
    if (!translations[lang]) {
        console.error(`Language "${lang}" not found`);
        return;
    }
    
    const translation = translations[lang];
    
    // Update all elements with IDs
    Object.keys(translation).forEach(key => {
        const element = document.getElementById(key);
        if (element) {
            element.innerHTML = translation[key];
        }
    });
    
    // Update HTML lang attribute
    document.documentElement.lang = lang === 'kz' ? 'kk' : lang;
}

// Switch language and save preference
function switchLanguage(lang) {
    updatePageLanguage(lang);
    saveLanguage(lang);
}

// =====================================
// Language Switcher Event Listeners
// ===================================== 

document.addEventListener('DOMContentLoaded', function() {
    // Load saved language on page load
    const savedLang = getSavedLanguage();
    updatePageLanguage(savedLang);
    
    // Desktop language switchers
    const switchEn = document.getElementById('switch-en');
    const switchBg = document.getElementById('switch-bg');
    const switchRu = document.getElementById('switch-ru');
    const switchKz = document.getElementById('switch-kz');
    
    if (switchEn) {
        switchEn.addEventListener('click', function(e) {
        e.preventDefault();
        switchLanguage('en');
    });
    }
    
    if (switchBg) {
        switchBg.addEventListener('click', function(e) {
            e.preventDefault();
            switchLanguage('bg');
        });
    }
    
    if (switchRu) {
        switchRu.addEventListener('click', function(e) {
        e.preventDefault();
        switchLanguage('ru');
    });
    }

    if (switchKz) {
        switchKz.addEventListener('click', function(e) {
		e.preventDefault();
		switchLanguage('kz');
	});
    }
    
    // Mobile language switchers
    const mobileLangLinks = document.querySelectorAll('.mobile-lang-link');
    mobileLangLinks.forEach(link => {
        link.addEventListener('click', function(e) {
            e.preventDefault();
            const lang = this.getAttribute('data-lang');
            if (lang) {
                switchLanguage(lang);
            }
        });
    });
});

// =====================================
// Current Year Display
// ===================================== 

document.addEventListener('DOMContentLoaded', function() {
    const currentYearElement = document.getElementById('currentYear');
    if (currentYearElement) {
        currentYearElement.textContent = new Date().getFullYear();
    }
});

// =====================================
// Mobile Menu Toggle
// ===================================== 

document.addEventListener('DOMContentLoaded', function() {
    const mobileMenuToggle = document.getElementById('mobileMenuToggle');
    const mobileMenu = document.getElementById('mobileMenu');
    
    if (mobileMenuToggle && mobileMenu) {
        mobileMenuToggle.addEventListener('click', function() {
            mobileMenuToggle.classList.toggle('active');
            mobileMenu.classList.toggle('active');
        });
        
        // Close menu when clicking a link
        const mobileNavLinks = document.querySelectorAll('.mobile-nav-link, .mobile-lang-link');
        mobileNavLinks.forEach(link => {
            link.addEventListener('click', function() {
                mobileMenuToggle.classList.remove('active');
                mobileMenu.classList.remove('active');
            });
        });
    }
});

// =====================================
// Header Logo and Color Change on Scroll
// ===================================== 

document.addEventListener('DOMContentLoaded', function() {
    const header = document.querySelector('header');
    const headerLogo = document.querySelector('.header-logo');
    const containers = document.querySelectorAll('.container, .container-bg-gray, .container-bg-beige');
    
    if (!header || !headerLogo) return;
    
    function checkHeaderPosition() {
        const headerRect = header.getBoundingClientRect();
        const headerBottom = headerRect.bottom;
        
        let isOverContainer = false;
        
        // Check if header overlaps with any container
        containers.forEach(container => {
            const containerRect = container.getBoundingClientRect();
            // Check if header bottom is within the container area
            if (headerBottom > containerRect.top && headerBottom < containerRect.bottom) {
                isOverContainer = true;
            }
        });
        
        // Toggle dark theme when over container
        if (isOverContainer) {
            header.classList.add('header-dark');
            headerLogo.src = 'assets/Neuron-Prime-Logo.png';
        } else {
            header.classList.remove('header-dark');
            headerLogo.src = 'assets/Neuron-Prime-Logo-Dark-Bg.png';
        }
    }
    
    // Check on scroll
    window.addEventListener('scroll', checkHeaderPosition);
    // Check on load
    checkHeaderPosition();
});
